const gulp = require('gulp');
const conf = require('../config');

/* copy assets like images */
gulp.task('copy-assets', ['copy-gif', 'copy-icon'], function() {
    return gulp.src(['src/assets/**/*'])
        .pipe(gulp.dest(conf.path.dist('assets')));
});

/* copy ericsson-loading.gif */
gulp.task('copy-gif', function() {
    return gulp.src(['node_modules/@ericsson/oden/assets/images/ericsson-loading.gif'])
        .pipe(gulp.dest(conf.path.dist('assets')));
});

/* copy app icon */
gulp.task('copy-icon', function() {
    return gulp.src(['node_modules/@ericsson/cus-ui/boilerplate/favicon.ico'])
        .pipe(gulp.dest(conf.path.dist()));
});