import { Component, AfterViewInit } from '@angular/core';
import { UniversalService } from '@ericsson/oden';


@Component({
    selector: 'cus-universal-overlay',
    template: `<div></div>`
})
export class UniversalOverlayComponent implements AfterViewInit {

    constructor(private _universalService: UniversalService) {
    }

    ngAfterViewInit() {
        this._universalService.showUniversalOverlay(null, false);
    }
}
