import { NgModule } from '@angular/core';
import { CustomRequestOptions } from './request.options';
import { RequestOptions } from '@angular/http';
import { AuthenticationStrategy } from './authentication_strategy';
import { HttpModule } from '@angular/http';
import { HttpClientModule } from '@angular/common/http';

@NgModule({
    imports: [HttpModule, HttpClientModule],
    providers: [{
        provide: RequestOptions,
        useFactory: requestOptionsFactory,
        deps: [AuthenticationStrategy]
    }]
})
export class AuthenticationModule {
}

export function requestOptionsFactory() {
    return new CustomRequestOptions();
}
