import { Injectable } from '@angular/core';
import { AuthToken } from './models';

@Injectable()
export class AuthenticationService {

    /**
     * get auth token
     */
    static getAuthToken(): AuthToken {
        let autToken = new AuthToken();
        autToken.tokenName = 'iPlanetDirectoryPro';
        autToken.token = this.getCookie(autToken.tokenName);
        return autToken;
    }

    /**
     * get cookie
     */
    static getCookie(cookieKey: string) {
        let name = cookieKey + '=';
        let decodedCookie = decodeURIComponent(document.cookie);
        let ca = decodedCookie.split(';');
        for (let i = 0; i < ca.length; i++) {
            let c = ca[i];
            while (c.charAt(0) === ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) === 0) {
                return c.substring(name.length, c.length);
            }
        }
        return '';
    }

    constructor() {}

}
