import { NgModule, ModuleWithProviders } from '@angular/core';
import { FunctionControlCacheService } from './function-control-cache.service';
import { FunctionControlService } from './function-control.service';

@NgModule({
    providers: [],
    entryComponents: []
})
export class FunctionControlModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: FunctionControlModule,
            providers: [FunctionControlCacheService, FunctionControlService]
        };
    }
}
