import { FunctionControlStatusResult } from './function-control-status-result';

export class FunctionControlStatus {

    private data: Map<string, boolean> = new Map<string, boolean>();

    constructor(results: FunctionControlStatusResult[]) {
        if (results && results.length > 0) {
            results.forEach((item) => {
                this.data.set(item.functionId, item.isActive);
            })
        }
    }

    /**
    * Get status of each functionId
    * @param fuctionId
    */
    get(functionId: string): boolean {
        if (this.data.has(functionId)) {
            return this.data.get(functionId);
        }
        return false;
    }
}
