import { NgModule, ModuleWithProviders } from '@angular/core';
import { UserPersonalizationStrategy } from './user-personalization.strategy';
import { PersonalizationStrategy } from './personalization.strategy';

@NgModule({
    providers: []
})
export class PersonalizationModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: PersonalizationModule,
            providers: [
                { provide: PersonalizationStrategy, useClass: UserPersonalizationStrategy },
            ]
        };
    }
}

