import { Observable } from 'rxjs/Rx';
import { UserPreferenceModel } from './user-preference.model';

export abstract class PersonalizationStrategy {

    /**
     * Set Favorites
     * @param user
     */
    abstract setFavorites(userName: string, favorites: string[]): void;

    /**
     * Get Favorites
     * @param user
     */
    abstract getFavorites(userName: string): Observable<string[]>;

    /**
     * Gets the user preferences.
     * @param userName
     */
    abstract getPreferences(userName: string): UserPreferenceModel;
}
