import { Injectable } from '@angular/core';
import { ApplicationTabService, TabsItem } from '../tabs';
import { FunctionControlStatus } from '../function-control/models';
import { FunctionControlService } from '../function-control';
import { Observable } from 'rxjs';

@Injectable()
export class ApplicationService {

    constructor(private tabService: ApplicationTabService,
        private _functionControlService: FunctionControlService) { }

    /**
     * Get the current application TabsList.
     * @return Observable<Array<TabsItem>>
     */
    getAppTabs(): Observable<Array<TabsItem>> {
        return this.tabService.getAppTabs();
    }

    /**
     * Get function control active status
     * @param functionIds
     * @returns {Observable<FunctionControlStatus | boolean>}
     */
    isFunctionActive(functionIds: string[] | string): Observable<FunctionControlStatus | boolean> {
        return this._functionControlService.isFunctionActive(functionIds);
    }
}
