export abstract class Storage {
    /**
     * abstract class that sets value in local/session/custom storage and
     * accepts optional expire time in milliseconds
     * @param key
     * @param value
     * @param expires
     */
    abstract set(key: string, value: any, expires?: number): void;
    /**
     * abstract class that gets value from local/session/custom storage
     * @param key
     */
    abstract get(key: string): any;
    /**
     * abstract class that removes particular value from local/session/custom storage
     * @param key
     */
    abstract remove(key: string): void;
}
