import { ModuleWithProviders, NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { OdenModule, PieModule } from '@ericsson/oden';
import { AppchartComponent } from './appchart.component';

@NgModule({
    imports: [PieModule, OdenModule, CommonModule],
    declarations: [AppchartComponent],
    exports: [AppchartComponent]
})

export class AppChartModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: AppChartModule
        };
    }
}
