import { ModuleWithProviders, NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { OdenModule, PieModule } from '@ericsson/oden';
import { AreachartComponent } from './areachart.component';

@NgModule({
    imports: [PieModule, OdenModule, CommonModule],
    declarations: [AreachartComponent],
    exports: [AreachartComponent]
})

export class AreaChartModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: AreaChartModule
        };
    }
}
