import { ChartData, MainMenuRoute } from '@ericsson/oden/modules';

export class BCOModel {
    configName: string;
    value: number;
}

export class BCOChartData extends ChartData {
    key?: MainMenuRoute;
    selected?: boolean; // it should be added in oden ChartData model
    active?: boolean; // to identify active slice in chart
    externalApp?: string; // Name of external application.
}

export class BCOMainMenuRoute extends MainMenuRoute {
    configName?: string; // it should be added in oden MainMenuRoute model
}
