import { ModuleWithProviders, NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { OdenModule, PieModule } from '@ericsson/oden';
import { AppChartModule } from './appchart';
import { AreaChartModule } from './areachart';
import { EntityChartModule } from './entitychart';
import { BCOService, BCOConfigurationService } from './services';
import { BCOComponent } from './bco.component';
import { HttpClientModule } from '@angular/common/http';


@NgModule({
    imports: [PieModule, OdenModule, CommonModule,
        HttpClientModule,
        AppChartModule.forRoot(), AreaChartModule.forRoot(), EntityChartModule.forRoot()],
    declarations: [BCOComponent],
    exports: [BCOComponent]
})

export class BCOModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: BCOModule,
            providers: [BCOService, BCOConfigurationService]
        }
    }
}
