import { ModuleWithProviders, NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { OdenModule, PieModule } from '@ericsson/oden';
import { EntitychartComponent } from './entitychart.component';

@NgModule({
    imports: [PieModule, OdenModule, CommonModule],
    declarations: [EntitychartComponent],
    exports: [EntitychartComponent]
})

export class EntityChartModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: EntityChartModule
        };
    }
}
