import { ModuleWithProviders, NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { OdenModule } from '@ericsson/oden';
import { HttpClientModule } from '@angular/common/http';
import { EnhancedTableComponent } from './enhanced-table.component';
import { EnhancedTableService } from './enhanced-table.service';

@NgModule({
    imports: [CommonModule, OdenModule, HttpClientModule],
    declarations: [EnhancedTableComponent],
    providers: [EnhancedTableService],
    exports: [EnhancedTableComponent],
    entryComponents: [EnhancedTableComponent]
})

export class EnhancedTableModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: EnhancedTableModule
        }
    }
}
