import { Component, OnInit, Input } from '@angular/core';
import { List } from './list.model';
import { Router } from '@angular/router';
import { NavigationHelper } from '@ericsson/oden/core';

@Component({
  selector: 'cus-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent implements OnInit {
  public data = [];
  @Input() dataSet: Array<List> = [];
  @Input() headline: string;
  @Input() multiline: boolean;
  @Input() columnSize: number;

  constructor(private router: Router) { }

  ngOnInit(): void {
    this.reload();
  }

  /**
  * Force reload when data changed
  */
  reload(): void {
    this.data = this.dataSet;
  }

  /**
   * Acts when list button is clicked
   * @param link
   */
  onListClick(link): void {
    if (link) {
      NavigationHelper.navigateToRoute(link, this.router);
    }
  }

}
