import { Component, OnInit, Input, HostBinding } from '@angular/core';
import { WidgetContainer } from './widget-container.model';
import { WidgetSet } from './widget-set/widget-set.model';

@Component({
  selector: 'cus-widget-container',
  templateUrl: 'widget-container.component.html'
})

export class WidgetContainerComponent implements OnInit {
  @Input() container: WidgetContainer;
  @Input() color: string;
  @HostBinding('class.widget-container') addClass: boolean = true;
  sets: WidgetSet[];

  constructor() {}

  ngOnInit() {
    this.sets = this.container.sets.map((set) => new WidgetSet(set));
  }
}
