import { WidgetContainer } from './widget-container/widget-container.model';
import { QuickAssign } from '../helpers/quick-assign.model';

export class Widget extends QuickAssign {
  private _apiPath: string;
  groupKey?: string;
  currentApi: string;
  name: string;
  appName: string;
  title: string;
  priority: number;
  size: number;
  description: string;
  containers: WidgetContainer[];
  noDataTitle: string;
  type?: string;
  get apiPath() {
    if (this.currentApi) {
      return this.currentApi;
    } else if (this.containers && this.containers.length > 0 &&
      this.containers[0].sets && this.containers[0].sets.length > 0 &&
      this.containers[0].sets[0].dataSet && this.containers[0].sets[0].dataSet.length > 0) {
      return this.containers[0].sets[0].dataSet[0].path;
    }

    return this._apiPath;

  }
  set apiPath(path: string) {
    this._apiPath = path;
    this.currentApi = path;
  }
}
