/**
 * Logger Error.
 * @param error
 */

export class Error {
    level: string;
    message: string;
    url: string;
    stack: string;
    module: string;
    datetime: Date;

    public toString = (): string => {
        return `DateTime: ${this.datetime.toUTCString()},
        Module: ${this.module},
        Url: ${this.url},
        Message: ${this.message},
        StackTrace: ${this.stack}`;
    }
}
