# boilerplate html file copy to src
cp node_modules/@ericsson/cus-ui/boilerplate/index.html src/
cp node_modules/@ericsson/cus-ui/boilerplate/favicon.ico src/

# removes duplicated versions of oden
rm -rf node_modules/@ericsson/cus-ui/node_modules/@ericsson/oden

# removes duplicated versions of rxjs and angular
rm -rf node_modules/@ericsson/cus-ui/node_modules/rxjs
rm -rf node_modules/@ericsson/cus-ui/node_modules/@angular
rm -rf node_modules/@ericsson/cus-ui/node_modules/@ngtools

# copy all files from mock folder and avoid if destination folder already contains same file
test -d src/mocks || mkdir -p src/mocks && cp -r -u node_modules/@ericsson/cus-ui/boilerplate/mocks/* src/mocks/

# copy all files from i18n folder and avoid if destination folder already contains same file
test -d src/assets/i18n || mkdir -p src/assets/i18n && cp -r -u node_modules/@ericsson/cus-ui/boilerplate/i18n/* src/assets/i18n/
