import { Component, OnInit } from '@angular/core';
import { WIZARD_TYPE } from '@ericsson/oden/core/constants';
import { WizardStep } from '@ericsson/oden';
import { SummaryItem } from '../../../models';
import { TaskConstants } from '../../../task.constants';
import { SummaryService } from '../../services/summary.service';

@Component({
  selector: 'cus-summary',
  templateUrl: './summary.component.html',
  styleUrls: ['./summary.component.scss']
})
export class SummaryComponent implements OnInit {
  wizardType: WIZARD_TYPE = WIZARD_TYPE.NON_LINEAR;
  step: WizardStep = new WizardStep();
  summaryItems: SummaryItem[];
  dateFormat: string = TaskConstants.DATE_FORMAT;
  constructor(private _summaryService: SummaryService) { }

  ngOnInit() {
    this.summaryItems = this._summaryService.summaryInfo;
  }

}
