import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';

import { GuideComponent, SummaryComponent, GuideChildComponent } from './components';
import { TaskErrorComponent } from './components/error';
import { CanDeactivateGuard } from './services/can.deactivate.service';

export const routes: Routes = [
    {
        path: ':workflowId/:processId', component: GuideComponent,
        children: [
            { path: '', component: GuideChildComponent },
            { path: 'summary', component: SummaryComponent },
            { path: '**', component: TaskErrorComponent }
        ],
        canDeactivate: [CanDeactivateGuard]
    }
];

@NgModule({
    imports: [RouterModule.forChild(routes)],
    exports: [RouterModule]
})
export class GuideRoutingModule { }
