import { Injectable } from '@angular/core';
import { CanDeactivate, ActivatedRouteSnapshot } from '@angular/router';
import { Observable } from 'rxjs/Observable';

export interface CanComponentDeactivate {
    canDeactivateRoute: () => Observable<boolean> | Promise<boolean> | boolean;
}

@Injectable()
export class CanDeactivateGuard implements CanDeactivate<CanComponentDeactivate> {
    canDeactivate(component: CanComponentDeactivate, activatedRouteSnapshot: ActivatedRouteSnapshot) {
        return component.canDeactivateRoute && !activatedRouteSnapshot.data.exitGuidedView
            ? component.canDeactivateRoute() : true;
    }
}
