import { SummaryItem } from '../../models/task-info.model';

/**
 * Service for setting and getting summary of Guide Screen
 */
export class SummaryService {
    private _summaryInfo: SummaryItem[] = [];

    /**
     * Set Summary
     * @param summary
     */
    set summaryInfo(summary) {
        this._summaryInfo = summary;
    }

    /**
     * Get Summary
     */
    get summaryInfo() {
        return this._summaryInfo;
    }

    constructor() { }
}
