import { Observable } from 'rxjs';
import { TaskInfo, TaskData } from '../models';
import { TaskContextService } from '../guide';

/**
 * Application Class has to implement TaskScreen interface in order to start task life cycle hooks
 */
export interface TaskScreen {

    /**
     * Task Lifecycle hook that is called after angular ngOnInit
     */
    taskOnInit(): void;

    /**
     * Task Lifecycle hook that is called after taskOnInit
     * The `taskInfo` and `taskContext` parameters contains task related information
     */
    taskOnLoad(taskInfo: TaskInfo, context: TaskContextService): void;

    /**
     * Task Lifecycle hook that is called once any screen is submitted
     */
    taskOnSubmit(): Observable<TaskData>;
}
