export class TaskRegistrationModel {
    tpg: string;
    screenPath: string;
    uri: TaskURIModel

    constructor(tpg, screenPath, uri) {
        this.tpg = tpg;
        this.screenPath = screenPath;
        this.uri = uri;
    }
}

export class TaskURIModel {
    list: string;
    assign: string;
    revoke: string;

    constructor(list, assign, revoke) {
        this.list = list;
        this.assign = assign;
        this.revoke = revoke;
    }
}
