import { animate, state, style, transition, trigger } from '@angular/animations';
import { ANIMATION } from '@ericsson/oden/core/constants';

export enum TASK_TAB_ANIMATION_STATES {
    MOVE_IN_LEFT = 'move-in-left',
    MOVE_IN_RIGHT = 'move-in-right',
    MOVE_OUT_LEFT = 'move-out-left',
    MOVE_OUT_RIGHT = 'move-out-right',
    VOID = 'void'
}

export const TASK_TAB: { ANIMATION: any } = {
    ANIMATION: trigger('state', [
        state('*', style({ opacity: 1 })),
        state(TASK_TAB_ANIMATION_STATES.MOVE_OUT_LEFT, style({ transform: 'translateX(-130%)', opacity: 0 })),
        state(TASK_TAB_ANIMATION_STATES.MOVE_OUT_RIGHT, style({ transform: 'translateX(+130%)', opacity: 0 })),
        state(TASK_TAB_ANIMATION_STATES.VOID, style({ opacity: 0 })),

        transition(`* => ${TASK_TAB_ANIMATION_STATES.MOVE_OUT_RIGHT}`, animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)),
        transition(`* => ${TASK_TAB_ANIMATION_STATES.MOVE_OUT_LEFT}`, animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)),

        transition(`void => ${TASK_TAB_ANIMATION_STATES.MOVE_IN_LEFT}`, [
            style({ transform: 'translateX(-130%)', opacity: 1 }),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),

        transition(`void => ${TASK_TAB_ANIMATION_STATES.MOVE_IN_RIGHT}`, [
            style({ transform: 'translateX(+130%)', opacity: 1 }),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ])
    ]
    )
};

export const TASK_LIST: { FADE: any } = {
    FADE: trigger('fadeInOut', [
        transition(':enter', [
            style({ opacity: 0 }),
            animate(200, style({ opacity: 1 }))
        ]),
        transition(':leave', [
            animate(200, style({ opacity: 0 }))
        ])
    ])
}

