/**
    This class is used for getting task context
 */
export class ContextDataItem {
    key: string;
    value: any;
    type?: string
}


export class ContextData {
    private _data: any;

    constructor(context) {
        this._data = context;
    }

    /**
    * Get value of context key
    * @param key
    */
    get(key: string) {
        return this._data && this._data[key] ? this._data[key] : null;
    }
}
