import { TaskSummaryItem } from './task-summary-item.model';

/**
 * This class is used to represent task information
 */
export class TaskInfo {
    name: string;
    processDefinitionId: string;
    taskId: string;
    screenPath: string;
    state: string;
    instanceId: string;
    workflowName: string;
    summary?: SummaryItem[];
    nextTask: string;
    subProcessSequence?: string[];
}

export class SummaryItem {
    taskName: string;
    summaryItems: TaskSummaryItem[];
    completedDate: string;
    username: string;
}
