import { Injectable } from '@angular/core';
import { EventEmitter } from '@angular/core';
import { TaskScreen } from './interface';
import { AppHeaderService } from '@ericsson/oden/modules';

@Injectable()
export class TaskScreenService {
    private _currentTaskScreen: TaskScreen;
    private _currentAppHeaderService: AppHeaderService;
    public onScreenLoad$: EventEmitter<string> = new EventEmitter<string>();
    constructor(
    ) {
    }

    /**
     * Sets current task screen
     * @param currentTaskScreen
     */
    set currentTaskScreen(currentTaskScreen) {
        this._currentTaskScreen = currentTaskScreen;
    }

    /**
     * Gets current task screen
     */
    get currentTaskScreen() {
        return this._currentTaskScreen;
    }

    /**
     * Sets current appheader of any application screen
     * @param appHeader
     */
    set currentAppHeaderService(appHeader) {
        this._currentAppHeaderService = appHeader;
    }

    /**
     * Gets current appheader of any application screen
     */
    get currentAppHeaderService() {
        return this._currentAppHeaderService;
    }

    /**
     * Emits even whenever screen reload happens
     */
    update() {
        this.onScreenLoad$.emit();
    }
}
