import { Injectable, InjectionToken, Inject } from '@angular/core';
import { Observable } from 'rxjs';
import { Definitions } from './guide/models';
import { HttpClient } from '@angular/common/http';
export const WORKFLOW_DEFINITION_URL = new InjectionToken<string>('workflow_definition_url');

/**
 * UserJourney Service that is exposed to applications
 */
@Injectable()
export class UserJourneyService {

    constructor(private http: HttpClient,
        @Inject(WORKFLOW_DEFINITION_URL) private workflowDefinitionUrl: string
    ) {
    }

    /**
     * API returns list of workflow definitions
     * @returns {Observable<WorkflowDefinitionModel>}
     *
     */
    getDefinitions(): Observable<Definitions[]> {
        return this.http.get<Definitions[]>(this.workflowDefinitionUrl)
            .catch(this.handleError);
    }

    /**
     *
     * @param error
     */
    private handleError(error: Response) {
        return Observable.throw(error);
    }

}
