import { Component, ViewEncapsulation, HostBinding, Input } from '@angular/core';

@Component({
    selector: 'cui-action-menu-item',
    templateUrl: 'action-menu-item.component.html',
    styleUrls: ['action-menu-item.scss'],
    encapsulation: ViewEncapsulation.None
})

export class ActionMenuItemComponent {
    @HostBinding('class.cui-action-menu-item') addClass: boolean = true;
    @HostBinding('class.disabled') @Input() isDisabled: boolean = false;
    @HostBinding('class.divider') @Input() isDivider: boolean = false;
    @HostBinding('class.label') @Input() isLabel: boolean = false;
    @Input() isAction: boolean = false;
    @Input() label: string;
    @Input() icon: string;
    @Input() tooltip: string;

    constructor() { }
}
