import { Component, EventEmitter, HostBinding, HostListener, ViewEncapsulation } from '@angular/core';
import { BACKDROP } from './backdrop.constants';

@Component({
    selector: 'cui-backdrop',
    template: '',
    styleUrls: ['backdrop.component.scss'],
    encapsulation: ViewEncapsulation.None,
    animations: BACKDROP.BACKDROP_ANIMATION
})

export class BackdropComponent {
    @HostBinding('class.cui-backdrop') addClass: boolean = true;
    @HostBinding('style.z-index') zIndex: number;
    @HostBinding('@visibility') visibility = 'visible';
    @HostBinding('class.hidden') isHidden: boolean = true;

    public onClose: EventEmitter<BackdropComponent> = new EventEmitter<BackdropComponent>();

    // Emits onClose event when clicking on backdrop.
    @HostListener('click')
    click(): void {
        this.onClose.emit(this);
    }
}
