import { style, animate, transition, state, trigger } from '@angular/animations';

// Animation for appearance and disappearance of backdrop
export const BACKDROP: { BACKDROP_ANIMATION: any[] } = {
    BACKDROP_ANIMATION: [
        trigger('visibility', [
            state('*', style({ opacity: 1 })),
            transition('void => visible', [
                style({ opacity: 0 }),
                animate('100ms')
            ]),
            transition('visible => void', animate('100ms', style({ opacity: 0 })))
        ])
    ]
};
