import { CommonModule } from '@angular/common';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { BackdropComponent } from './backdrop.component';
import { BackdropHiddenComponent } from './backdrop-hidden.component';
import { BackdropService } from './backdrop.service';

@NgModule({
    imports: [ CommonModule ],
    exports: [ BackdropComponent, BackdropHiddenComponent ],
    declarations: [ BackdropComponent, BackdropHiddenComponent ],
    entryComponents: [ BackdropHiddenComponent, BackdropComponent ],
    providers: [ BackdropService ]
})

export class BackdropModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: BackdropModule,
            providers: [ BackdropService ]
        };
    }
}
