import { CommonModule } from '@angular/common';
import { ModuleWithProviders, NgModule } from '@angular/core';
import { BatchActionsComponent } from './batch-actions.component';
import { TranslateModule } from '@ngx-translate/core';
import { FormModule } from '../form';
import { IconModule } from '../icon';
import { TooltipModule } from '../tooltip';
import { ButtonModule } from '../button';

@NgModule({
    imports: [CommonModule, TranslateModule.forChild(), FormModule, IconModule, TooltipModule, ButtonModule],
    exports: [BatchActionsComponent],
    declarations: [BatchActionsComponent]
})

export class BatchActionsModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: BatchActionsModule
        };
    }
}

