import { CommonModule } from '@angular/common';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { BucketVisualisationComponent } from './bucket-visualisation.component';
import { FormsModule } from '@angular/forms';
import { FormModule } from '../form';
import { ContainerModule } from '../container';
import { TooltipModule } from '../tooltip';
import { TranslateModule } from '@ngx-translate/core';

@NgModule({
    imports: [CommonModule, FormsModule, FormModule, ContainerModule, TooltipModule, TranslateModule.forChild()],
    exports: [BucketVisualisationComponent],
    declarations: [BucketVisualisationComponent]
})

export class BucketVisualisationModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: BucketVisualisationModule
        };
    }
}
