import { CommonModule } from '@angular/common';
import { ButtonComponent } from './button.component';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { IconModule } from '../icon/icon.module';
import {ButtonWrapperDirective} from './button-wrapper/button-wrapper.directive';
import { TooltipModule } from '../tooltip/tooltip.module';

@NgModule({
    imports: [CommonModule, IconModule, TooltipModule],
    exports: [ButtonComponent, ButtonWrapperDirective],
    declarations: [ButtonComponent, ButtonWrapperDirective],
})

export class ButtonModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: ButtonModule
        };
    }
}
