import { ChartService } from './../../chart.service';
import { CommonModule } from '@angular/common';
import { LegendAddonComponent } from './legend-addon.component';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { ContainerModule } from '../../../container';
import { TooltipModule } from '../../../tooltip';
import { IconModule } from '../../../icon/icon.module';

@NgModule({
    imports: [CommonModule, ContainerModule, TooltipModule, IconModule],
    exports: [LegendAddonComponent],
    declarations: [LegendAddonComponent],
})

export class LegendAddonModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: LegendAddonModule,
            providers: [ChartService]
        };
    }
}
