import { Component, HostBinding, Input, ViewEncapsulation } from '@angular/core';
import { ApplicationService } from '../../../../core/services/application.service';

@Component({
    selector: 'cui-link-addon',
    templateUrl: 'link-addon.component.html',
    styleUrls: ['link-addon.component.scss'],
    encapsulation: ViewEncapsulation.None
})

export class LinkAddonComponent {
    @HostBinding('class.cui-link-addon') addClass: boolean = true;

    @Input() path: string;
    @Input() label: string;

    constructor(private _applicationService: ApplicationService) { }

    /**
     * Navigates to the absolute path.
     * @param path
     */
    navigateToPath(path: string): void {
        this._applicationService.navigateToPath(path);
    }
}
