import { NgModule, ModuleWithProviders } from '@angular/core';
import { TranslateModule } from '@ngx-translate/core';
import { CommonModule } from '@angular/common';
import { ChartService } from './../../chart.service';
import { ContainerModule } from '../../../container';
import { SelectorAddonComponent } from './selector-addon.component';
import { FormModule } from '../../../form/form.module';

@NgModule({
    imports: [CommonModule, ContainerModule, FormModule, TranslateModule.forChild()],
    exports: [SelectorAddonComponent],
    declarations: [SelectorAddonComponent],
})

export class SelectorAddonModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: SelectorAddonModule,
            providers: [ChartService]
        };
    }
}
