import { ChartService } from './../../chart.service';
import { CommonModule, DecimalPipe } from '@angular/common';
import { SumAddonComponent } from './sum-addon.component';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { ContainerModule } from '../../../container';
import { IconModule } from '../../../icon/icon.module';

@NgModule({
    imports: [CommonModule, ContainerModule, IconModule],
    exports: [SumAddonComponent],
    declarations: [SumAddonComponent],
    providers: [DecimalPipe]
})

export class SumAddonModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: SumAddonModule,
            providers: [ChartService]
        };
    }
}
