import { ChartService } from './../../chart.service';
import { CommonModule } from '@angular/common';
import { TrendAddonComponent } from './trend-addon.component';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { ContainerModule } from '../../../container';

@NgModule({
    imports: [CommonModule, ContainerModule],
    exports: [TrendAddonComponent],
    declarations: [TrendAddonComponent],
})

export class TrendAddonModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: TrendAddonModule,
            providers: [ChartService]
        };
    }
}
