import { CommonModule, DecimalPipe } from '@angular/common';
import { ModuleWithProviders, NgModule } from '@angular/core';

import { ContainerModule } from '../../container/container.module';
import { AddonModule } from '../addon/addon.module';
import { RainbowChartComponent } from './rainbow-chart.component';

@NgModule({
    imports: [CommonModule, ContainerModule, AddonModule],
    declarations: [RainbowChartComponent],
    exports: [RainbowChartComponent],
    providers: [DecimalPipe]
})

export class RainbowChartModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: RainbowChartModule
        };
    }
}
