import { CommonModule } from '@angular/common';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { ContainerComponent } from './container.component';
import { RowComponent } from './row/row.component';
import { ColumnComponent } from './row/column/column.component';
import { ColumnSizeDirective } from './row/column/column-size.directive';
import { ActionMenuModule } from '../action-menu/action-menu.module';

@NgModule({
    imports: [CommonModule, ActionMenuModule],
    exports: [ContainerComponent, RowComponent, ColumnComponent, ColumnSizeDirective],
    declarations: [ContainerComponent, RowComponent, ColumnComponent, ColumnSizeDirective]
})

export class ContainerModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: ContainerModule
        };
    }
}

