import { CommonModule } from '@angular/common';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { ElementOverlayDirective } from './element-overlay.directive';
import { ElementErrorOverlayComponent } from './error-overlay/error-overlay.component';
import { LoaderComponent } from '../loader/loader.component';
import { ButtonModule } from '../button/button.module';
import { IconModule } from '../icon/icon.module';
import { LoaderModule } from '../loader/loader.module';

@NgModule({
    imports: [CommonModule, ButtonModule, IconModule, LoaderModule],
    exports: [ElementOverlayDirective, ElementErrorOverlayComponent],
    declarations: [ElementOverlayDirective, ElementErrorOverlayComponent],
    entryComponents: [ElementErrorOverlayComponent, LoaderComponent]
})

export class ElementOverlayModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: ElementOverlayModule
        };
    }
}

