import { Component, Input, HostBinding, ViewEncapsulation } from '@angular/core';
import { GroupTitleAttribute } from '../../core/models/group-title.attribute';
import { ALIGNMENT } from '../../core/constants/alignment.constants';

@Component({
    selector: 'cui-group-title',
    templateUrl: './group-title.component.html',
    styleUrls: ['./group-title.component.scss'],
    encapsulation: ViewEncapsulation.None
})

export class GroupTitleComponent {
    @Input() groupTitle: string ;
    @Input() attributes?: Array<GroupTitleAttribute> = [];
    @Input() groupIcon?: string;
    @HostBinding('class.cui-group-title') addClass: boolean = true;
    alignRight = ALIGNMENT.RIGHT;
    constructor() {}
}
