import { CommonModule } from '@angular/common';
import { ModuleWithProviders, NgModule } from '@angular/core';
import { ImageContainerComponent } from './image-container.component';
import { ImageAttributionDirective } from './attribution/attribution.directive';


@NgModule({
    imports: [CommonModule],
    exports: [ImageContainerComponent, ImageAttributionDirective],
    declarations: [ImageContainerComponent, ImageAttributionDirective],
})

export class ImageContainerModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: ImageContainerModule
        };
    }
}
