import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { IndicateDraggingDirective } from './indicate-dragging.directive';

@NgModule({
    imports: [CommonModule],
    exports: [IndicateDraggingDirective],
    declarations: [IndicateDraggingDirective]
})

export class IndicateDraggingModule {
    static forRoot() {
        return {
            ngModule: IndicateDraggingModule
        };
    }
}
